    assert(m === 0 ,'array must be 1d');
    var avg = average(arr);
    var variance = _a.reduce(arr, function(sum, v){ return sum + Math.pow(v - avg, 2); }, 0) / n;
    return Math.pow(variance, 0.5);
};

